import React, {useState, useRef} from "react";

import Card from '../UI/Card';
import classes from './AddUser.module.css'
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";

const  AddUser = props => {

    const nameInputRef = useRef();
    const ageInputRef = useRef();

    const [enteredUsername, setEnteredUsername] = useState('');
    const [enteredAge, setEnteredAge] = useState('');
    const [error, setError] = useState(null);

    const usernameChangeHanbeler = event => {
        setEnteredUsername(event.target.value);
    };
    const ageChangeHanbeler = event => {
        setEnteredAge(event.target.value);
    };
    const errorHadler = () => {
        setError(null);
    };

    const addUserHandler = (event) => {
        event.preventDefault();
        if(enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
            setError({
                title: 'Invalid input', 
                message: 'Please enter a valid name and age (non-empty values)'
            });
            console.log("invalid");
            return;
        }
        if(+enteredAge < 1) {
            setError({
                title: 'Invalid input', 
                message: 'Please enter a valid age (> 0)'
            });
            console.log("invalid");
            return;
        }

        //console.log(enteredUsername, enteredAge);
        props.onAddUser(enteredUsername, enteredAge);

        setEnteredUsername('');
        setEnteredAge('');
    };

    return (
        <React.Fragment>
            {error && <ErrorModal title={error.title} message={error.message} onConfirm={errorHadler}/>}
            <Card className={classes.input}>
                <form onSubmit={addUserHandler}>
                    <label htmlFor="username">Username</label>
                    <input id='uesername' type='text' value={enteredUsername} onChange={usernameChangeHanbeler}/>

                    <label htmlFor='age'>Age(year)</label>
                    <input id='age' type='number' value={enteredAge} onChange={ageChangeHanbeler}/>

                    <Button type="submit">Add User</Button>
                </form>
            </Card>
        </React.Fragment>
    );

};

export default AddUser;
