import React, {useState} from 'react';
import './Expenses.css';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';

const Expenses = (props) => {

    const [filteredYear, setFilteredyear] = useState('2020');
    const filterChangeHandler = (selectedYear) => {
        setFilteredyear(selectedYear);
    };

    // list of the expenses passed to this function filtered by year
    const filteredExpenses = props.expenses.filter(exp => {
        return exp.date.getFullYear().toString() === filteredYear;
    }); 

    let expenseContent = <p>No expenses found!!</p>;
    if(filteredExpenses > 0) {
         // this how we render a list of elemen. we map each element in the array to a component
        // when maping a list of items we need to provide the item id as prop; this allows to improve performace
        // and also avoid bugs. with out the id React won't be able to tell the difference between the items
        expenseContent = filteredExpenses.map(exp => <ExpenseItem key={exp.id} title={exp.title} amount={exp.amount} date={exp.date}/>);
    }
 
    return (
        <div>
            <Card className='expenses'>
                <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHandler}/>
                {expenseContent}
            </Card>
        </div>
    );
}

export default Expenses;
