import React, {useState} from 'react';
import './Expenses.css';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';

const Expenses = (props) => {

    const [filteredYear, setFilteredyear] = useState('2020');
    const filterChangeHandler = (selectedYear) => {
        setFilteredyear(selectedYear);
    };

    const expenses = props.expenses; // list of the expenses passed to this function

    return (
        <div>
            <Card className='expenses'>
                <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHandler}/>
                {
                    // this how we render a list of elemen. we map each element in the array to a component
                    expenses.map(exp => <ExpenseItem key={exp.id} title={exp.title} amount={exp.amount} date={exp.date}/>)
                }
            </Card>
        </div>
    );
}

export default Expenses;
